mapper.select:
select t.*,team.* from competition_of_team t left join basketball_team team on team.id = t.team_id

     and t.competition_id = #{competitionId}
     and t.team_id = #{teamId}
     and t.team_name like concat('%', #{teamName}, '%')
     and t.competition_group = #{competitionGroup}
     and t.created_time = #{createdTime}
     and t.status = #{status}
     and t.last_updated_time = #{lastUpdatedTime}
     and t.created_by = #{createdBy}
     and t.modified_by = #{modifiedBy}
     and t.is_deleted = #{isDeleted}
     and t.contacts = #{contacts}
     and t.contacts_tel = #{contactsTel}
     and t.contacts_area_code = #{contactsAreaCode}
     and t.serial_number = #{serialNumber}

select t.*,"" as teamImg,t.created_by as teamManagerUserId
from competition_of_team t where 1=1 and t.is_deleted=0

    AND  t.id=#{id}


    AND  t.is_deleted = #{isDeleted}


    AND  t.competition_id=#{competitionId}


    AND  t.status =#{status}


    AND  t.team_id =#{teamId}


    AND  t.competition_group =#{competitionGroup}


    AND  t.team_name like  CONCAT('%',#{teamName},'%')


    AND  t.contacts like  CONCAT('%',#{contacts},'%')


    AND  t.contacts_tel like  CONCAT('%',#{contactsTel},'%')


    AND  t.contacts_area_code like  CONCAT('%',#{contactsAreaCode},'%')

order by t.created_time desc

select t.*,"" as teamImg,t.created_by as teamManagerUserId
from competition_of_team t where 1=1 and t.is_deleted=0

    AND  t.id=#{id}


    AND  t.is_deleted = #{isDeleted}


    AND  t.competition_id=#{competitionId}


    AND  t.status =#{status}


    AND  t.team_id =#{teamId}


    AND  t.competition_group =#{competitionGroup}


    AND  t.team_name like  CONCAT('%',#{teamName},'%')


    AND  t.contacts like  CONCAT('%',#{contacts},'%')


    AND  t.contacts_tel like  CONCAT('%',#{contactsTel},'%')


    AND  t.contacts_area_code like  CONCAT('%',#{contactsAreaCode},'%')

order by t.created_time desc

select t.*,"" as teamImg,t.created_by as teamManagerUserId
from competition_of_team t where 1=1 and t.is_deleted=0

    AND  t.id=#{id}


    AND  t.is_deleted = #{isDeleted}


    AND  t.competition_id=#{competitionId}


    AND  t.status =#{status}


    AND  t.team_id =#{teamId}


    AND  t.competition_group =#{competitionGroup}


    AND  t.team_name like  CONCAT('%',#{teamName},'%')


    AND  t.contacts like  CONCAT('%',#{contacts},'%')


    AND  t.contacts_tel like  CONCAT('%',#{contactsTel},'%')


    AND  t.contacts_area_code like  CONCAT('%',#{contactsAreaCode},'%')

order by t.created_time desc

mapper.update:
update competition_of_team
competition_id = #{competitionId}, team_id = #{teamId}, team_name = #{teamName}, competition_group = #{competitionGroup}, created_time = #{createdTime}, status = #{status}, last_updated_time = #{lastUpdatedTime}, created_by = #{createdBy}, modified_by = #{modifiedBy}, is_deleted = #{isDeleted}, remark = #{remark}, contacts = #{contacts}, contacts_tel = #{contactsTel}, contacts_area_code = #{contactsAreaCode}, serial_number = #{serialNumber},
where id = #{id}
