mapper.select:
select t.id as id, t.competition_id as competitionId, t.main_team_id as mainTeamId, b1.TEAM_NAME as mainTeamName, b1.team_logo as mainTeamLogo, t.guest_team_id as guestTeamId, b2.TEAM_NAME as guestTeamName, b2.team_logo as guestTeamLogo, t.competition_time as competitionTime, t.building_id as buildingId, t.building_name as buildingName, t.competition_address as competitionAddress, t.competition_group as competitionGroup, t.status, t.vs_type as vsType, t.created_time as createdTime, t.last_updated_time as lastUpdatedTime, t.created_by as createdBy, t.modified_by as modifiedBy, t.main_team_score as mainTeamScore, t.guest_team_score as guestTeamScore, t.is_deleted as isDeleted, DATE_FORMAT(t.competition_time,'%Y-%m-%d') as competitionDate, CASE dayofweek(t.competition_time) WHEN 1 THEN '星期日' WHEN 2 THEN '星期一' WHEN 3 THEN '星期二' WHEN 4 THEN '星期三' WHEN 5 THEN '星期四' WHEN 6 THEN '星期五' WHEN 7 THEN '星期六' END as weekDayName, CASE t.status WHEN -1 THEN '已取消' WHEN 0 THEN '报名中' WHEN 1 THEN '比赛中' WHEN 2 THEN '已结束' END as statusName, DATE_FORMAT(t.competition_time,'%H:%i') AS theTime, t.remark as remark from competition_team_vs_team t LEFT JOIN competition_of_team b1 ON b1.id = t.main_team_id left join competition_of_team b2 on t.guest_team_id=b2.id where 1=1
AND t.id=#{id}
AND t.status =#{status}
AND t.is_deleted =#{isDeleted}
AND t.competition_id=#{competitionId}
AND t.building_name like CONCAT('%',#{buildingName},'%')
AND t.competition_group = #{competitionGroup}
AND t.main_team_name like CONCAT('%',#{mainTeamName},'%')
AND t.guest_team_name like CONCAT('%',#{guestTeamName},'%')
AND t.competition_address like CONCAT('%',#{competitionAddress},'%')
AND t.building_name like CONCAT('%',#{buildingName},'%')
order by t.competition_time asc

select cpt.competition_begin_time as theTime, team1.team_logo as mainTeamLogo, team2.team_logo as guestTeamLogo, t.* from competition cpt INNER JOIN competition_team_vs_team t on t.competition_id = cpt.id and t.is_deleted=0 LEFT JOIN basketball_team team1 on t.main_team_id = team1.ID and team1.IS_DELETED=0 LEFT JOIN basketball_team team2 on t.main_team_id = team2.ID and team2.IS_DELETED=0 where cpt.is_deleted=0
AND t.main_team_id=#{mainTeamId}
AND t.guest_team_id =#{guestTeamId}
order by t.competition_time desc limit 0,3

SELECT ANY_VALUE ( t.team_name ) as teamName, any_value (team.team_logo) as teamLogo, any_value (team.competition_group) as competition_group, t.competition_of_team_id, sum(IF( t.vs_result = 'win', 1, 0 )) AS win, sum(IF( t.vs_result = 'fail', 1, 0 )) AS fail, sum(IFNULL( t.one_node_score, 0 )+ IFNULL( t.two_node_score, 0 )+ IFNULL( t.three_node_score, 0 )+ IFNULL( t.four_node_score, 0 )+ IFNULL( t.five_node_score, 0 )+ IFNULL( t.six_node_score, 0 )) AS totalScore, sum(ifnull(t.integral,0)) AS integral , sum(IFNULL(t.net_win_point,0)) as netWinPoint FROM competition_result t left join competition_of_team team on team.id = t.competition_of_team_id WHERE t.is_deleted = 0 AND t.competition_id = #{competitionId}
AND t.vs_type =#{vsType}
AND t.competition_group =#{competitionGroup}
GROUP BY t.competition_of_team_id ORDER BY sum(t.integral) desc

select t.id as id, t.competition_id as competitionId, t.main_team_id as mainTeamId, b1.TEAM_NAME as mainTeamName, b1.team_logo as mainTeamLogo, t.guest_team_id as guestTeamId, b2.TEAM_NAME as guestTeamName, b2.team_logo as guestTeamLogo, t.competition_time as competitionTime, t.building_id as buildingId, t.building_name as buildingName, t.competition_address as competitionAddress, t.competition_group as competitionGroup, t.status, t.vs_type as vsType, t.created_time as createdTime, t.last_updated_time as lastUpdatedTime, t.created_by as createdBy, t.modified_by as modifiedBy, t.main_team_score as mainTeamScore, t.guest_team_score as guestTeamScore, t.is_deleted as isDeleted, DATE_FORMAT(t.competition_time,'%Y年%m月%d日') as competitionDate, CASE dayofweek(t.competition_time) WHEN 1 THEN '星期日' WHEN 2 THEN '星期一' WHEN 3 THEN '星期二' WHEN 4 THEN '星期三' WHEN 5 THEN '星期四' WHEN 6 THEN '星期五' WHEN 7 THEN '星期六' END as weekDayName, CASE t.status WHEN -1 THEN '已取消' WHEN 0 THEN '报名中' WHEN 1 THEN '比赛中' WHEN 2 THEN '已结束' END as statusName, DATE_FORMAT(t.competition_time,'%H:%i') AS theTime, t.remark as remark, u1.OPENID as guestTeamOpenId, u2.OPENID as mainTeamOpenId from competition_team_vs_team t LEFT JOIN competition_of_team b1 ON b1.id = t.main_team_id left join user_info u1 on u1.id=b1.created_by left join competition_of_team b2 on t.guest_team_id=b2.id left join user_info u2 on u2.id=b2.created_by where 1=1 and to_days(competition_time) <= to_days(now())
AND t.id=#{comp.id}
AND t.status =#{comp.status}
AND t.is_deleted =#{comp.isDeleted}
AND t.competition_id=#{comp.competitionId}
AND t.building_name like CONCAT('%',#{comp.buildingName},'%')
AND t.competition_group = #{comp.competitionGroup}
AND t.main_team_name like CONCAT('%',#{comp.mainTeamName},'%')
AND t.guest_team_name like CONCAT('%',#{comp.guestTeamName},'%')
AND t.competition_address like CONCAT('%',#{comp.competitionAddress},'%')
AND t.building_name like CONCAT('%',#{comp.buildingName},'%')
order by t.competition_time asc

mapper.update:
update competition_team_vs_team
competition_id = #{competitionId}, main_team_id = #{mainTeamId}, main_team_name = #{mainTeamName}, guest_team_id = #{guestTeamId}, guest_team_name = #{guestTeamName}, competition_time = #{competitionTime}, building_id = #{buildingId}, building_name = #{buildingName}, competition_address = #{competitionAddress}, competition_group = #{competitionGroup}, status = #{status}, created_time = #{createdTime}, last_updated_time = #{lastUpdatedTime}, created_by = #{createdBy}, modified_by = #{modifiedBy}, is_deleted = #{isDeleted}, remark = #{remark}, main_team_score = #{mainTeamScore}, guest_team_score = #{guestTeamScore}, vs_type = #{vsType}, batch_number = #{batchNumber},
where id = #{id}

update competition_team_vs_team set is_deleted = 1

     and competition_id = #{competitionId}
     and main_team_id = #{mainTeamId}
     and main_team_name like concat('%', #{mainTeamName}, '%')
     and guest_team_id = #{guestTeamId}
     and guest_team_name like concat('%', #{guestTeamName}, '%')
     and competition_time = #{competitionTime}
     and building_id = #{buildingId}
     and building_name like concat('%', #{buildingName}, '%')
     and competition_address = #{competitionAddress}
     and competition_group = #{competitionGroup}
     and status = #{status}
     and last_updated_time = #{lastUpdatedTime}
     and vs_type = #{vsType}
     and batch_number = #{batchNumber}
